@prefix geosrs: <https://w3id.org/geosrs/> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode geosrs:CylindricalCoordinateSystem ;
            sh:resultMessage "A cylindrical coordinate system should have at least three axes"@en ;
            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:CylindricalCoordinateSystemShape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:TemporalMeasureCoordinateSystem ;
            sh:resultMessage "A temporal measure coordinate system should have exactly one axis"@en ;
            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:TemporalMeasureCoordinateSystemShape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:TemporalCoordinateSystem ;
            sh:resultMessage "A temporal coordinate system should have exactly one axis"@en ;
            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:TemporalCoordinateSystemShape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:DateTimeTemporalCoordinateSystem ;
            sh:resultMessage "A date time temporal coordinate system should have exactly one axis"@en ;
            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:DateTimeTemporalCoordinateSystemShape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:1DCoordinateSystem ;
            sh:resultMessage "A 1D coordinate system should have exactly one axis"@en ;
            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:1DCoordinateSystemShape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:ConicalCoordinateSystem ;
            sh:resultMessage "A conical coordinate system should have at least three axes"@en ;
            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:ConicalCoordinateSystemShape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:CoordinateSystem ;
            sh:resultMessage "A coordinate system should have at least one axis"@en ;
            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:CoordinateSystemShape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:CurvilinearCoordinateSystem ;
            sh:resultMessage "A curvilinear coordinate system is defined in Euclidean space and should therefore have at least three axes"@en ;
            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:CurvilinearCoordinateSystemShape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:3DCoordinateSystem ;
            sh:resultMessage "A 3D coordinate system should have at least three axes"@en ;
            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:3DCoordinateSystemShape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:TemporalCountCoordinateSystem ;
            sh:resultMessage "A temporal count coordinate system should have exactly one axis"@en ;
            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:TemporalCountCoordinateSystemShape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:PlanarCoordinateSystem ;
            sh:resultMessage "A planar coordinate system should have at least two axes"@en ;
            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:PlanarCoordinateSystemShape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:CoordinateSystemAxis ;
            sh:resultMessage "A coordinate system axis should have exactly one axis direction"@en ;
            sh:resultPath geosrs:axisDirection ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:CoordinateSystemAxisShape_Property ] .

