@prefix geosrs: <https://w3id.org/geosrs/> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode geosrs:CompoundCRS ;
            sh:resultMessage "A compound coordinate reference system should consist of at least one single coordinate reference system"@en ;
            sh:resultPath geosrs:includesSRS ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:CompoundCRS_includesSRS_Shape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:GeodeticCRS ;
            sh:resultMessage "A geodetic coordinate reference system should have exactly one geodetic coordinate system"@en ;
            sh:resultPath geosrs:coordinateSystem ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:GeodeticCRS_coordinateSystem_Shape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:CRS ;
            sh:resultMessage "A coordinate reference system should have at least one area of use"@en ;
            sh:resultPath geosrs:domainOfValidity ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:CRS_domainOfValidity_Shape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:ParametricCRS ;
            sh:resultMessage "A parametric coordinate reference system should have exactly one parametric datum"@en ;
            sh:resultPath geosrs:datum ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:ParametricCRS_datum_Shape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:CRS ;
            sh:resultMessage "A coordinate reference system should have exactly one coordinate system"@en ;
            sh:resultPath geosrs:coordinateSystem ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:CRS_coordinateSystem_Shape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:TemporalCRS ;
            sh:resultMessage "A projected coordinate reference system should have exactly one temporal datum"@en ;
            sh:resultPath geosrs:datum ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:TemporalCRS_datum_Shape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:GeographicCRS ;
            sh:resultMessage "A geographic coordinate reference system should have exactly one ellipsoidal coordinate system"@en ;
            sh:resultPath geosrs:coordinateSystem ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:GeographicCRS_coordinateSystem_Shape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:GeographicCRS ;
            sh:resultMessage "A geographic coordinate reference system should have exactly one geodetic datum"@en ;
            sh:resultPath geosrs:datum ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:GeographicCRS_datum_Shape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:SingleCRS ;
            sh:resultMessage "A single coordinate reference system should have exactly one coordinate system"@en ;
            sh:resultPath geosrs:coordinateSystem ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:SingleCRS_coordinateSystem_Shape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:SingleCRS ;
            sh:resultMessage "A single coordinate reference system should have exactly one datum"@en ;
            sh:resultPath geosrs:datum ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:SingleCRS_datum_Shape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:ProjectedCRS ;
            sh:resultMessage "A projected coordinate reference system should have at least one conversion"@en ;
            sh:resultPath geosrs:conversion ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:ProjectedCRS_conversion_Shape_Property ] .
