            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape geosrs:CylindricalCoordinateSystem_axis_Shape_Property ],
        [ a sh:ValidationResult ;
            sh:focusNode geosrs:CurvilinearCoordinateSystem ;
            sh:resultMessage "A curvilinear coordinate system is defined in Euclidean space and should therefore have at least three axes"@en ;
            sh:resultPath geosrs:axis ;
            sh:resultSeverity sh:Violation ;
